dccread=0
dccwrite=1
dccloop=2

printf08x() {
    printf "0x%08x" "$1"
}

printfd() {
    printf "%d" "$1"
}

for ((i=0;$i<${#arr[@]};i+=4)); do
    case "${arr[$i]}" in
    0x00)
    dccop=DCC_READ
    dccarg1=$(printf08x "${arr[(($i+1))]}")
    dccarg2=$(printfd "${arr[(($i+2))]}")
    dccarg3=$(printfd "${arr[(($i+3))]}")
    ;;
    0x01)
    dccop=DCC_WRITE
    dccarg1=$(printf08x "${arr[(($i+1))]}")
    dccarg2=$(printf08x "${arr[(($i+2))]}")
    dccarg3=$(printfd "${arr[(($i+3))]}")
    ;;
    0x02)
    dccop=DCC_LOOP
    dccarg1=$(printfd "${arr[(($i+1))]}")
    dccarg2=$(printfd "${arr[(($i+2))]}")
    dccarg3=$(printfd "${arr[(($i+3))]}")
    ;;
    esac

    echo -e "\t\t\t\t <$dccop $dccarg1 $dccarg2 $dccarg3>,"
done
